async function checkLive(velyn) {
  if (!isCheckingLive) return

  try {
    const lives = await getLive(global.jkt48connect)
    if (!Array.isArray(lives)) return

    // Ambil nama-nama yang saat ini live
    let currentlyLive = new Set(lives.map(l => l.name))

    // Cek live yang sudah berakhir
    for (const [name, startTime] of liveSessions) {
      if (!currentlyLive.has(name)) {
        const durasi = getDuration(startTime)
        const endMsg = `츾춖츽춿츾축츽츾축츽춥츾축츽췂 *${name}* telah mengakhiri live.\n츾춖츽춱츾축츽춢츾축츽췀츾춖츽춾츾축츽췆츾축츽춢 *Durasi:* ${durasi}`
        for (const id of targetReceivers) {
          await velyn.sendMessage(id, { text: endMsg }).catch(e => console.log("츾춖츽춱츾축츽춯츾축츽춟 Gagal kirim end live:", e.message))
        }
        liveSessions.delete(name)
      }
    }

    // Proses live yang aktif sekarang
    for (const live of lives) {
      if (!live) continue

      if (live.type === 'youtube') {
        if (sentYouTubeEvents.has(live.title)) continue
        sentYouTubeEvents.add(live.title)

        for (const id of targetReceivers) {
          await velyn.sendMessage(id, { text: makeYoutubeMessage(live) })
        }

      } else {
        if (sentEvents.has(live.name)) continue
        const waktu = toWIB(live.started_at)
        sentEvents.add(live.name)
        liveSessions.set(live.name, Date.now())

        const caption = makeMessage(live, waktu)

        // Coba ambil poster image (jika ada linknya)
        const image = live.poster || live.image || null // ganti sesuai field aslinya kalau beda

        for (const id of targetReceivers) {
          if (image) {
            await velyn.sendMessage(id, {
              image: { url: image },
              caption
            }).catch(e => console.log(`츾춖츽춱츾축츽춯츾축츽춟 Gagal kirim poster ke ${id}:`, e.message))
          } else {
            await velyn.sendMessage(id, { text: caption })
          }
        }
      }
    }

  } catch (err) {
    console.error('[LIVE CHECK ERROR]', err.message)
  }

  // Loop ulang per 15 detik
  setTimeout(() => checkLive(velyn), 15000)
}

